var TableDatatablesManaged = function () {

    var initTable1 = function () {

        var table = $('#sample_1');

        // begin first table
        table.dataTable({
        "ajax": {
            "url": "https://openpaymentsdata.cms.gov/resource/v3nw-usd7.json?%24query=SELECT%20teaching_hospital_name%2C%20physician_profile_id%2C%20total_amount_of_payment_usdollars%2C%20nature_of_payment_or_transfer_of_value%2C%20date_of_payment%20WHERE%20nature_of_payment_or_transfer_of_value%20%3D%20'Gift'%20AND%20total_amount_of_payment_usdollars%20%3E%2010%20LIMIT%205",
        },

            "bStateSave": true, // save datatable state(pagination, sort, etc) in cookie.

            "lengthMenu": [
                [5, 15, 20, -1],
                [5, 15, 20, "All"] // change per page values here
            ],
            // set the initial value
            "pageLength": 5,            
            "pagingType": "bootstrap_full_number",
            "order": [
                [1, "asc"]
            ] // set first column as a default sort by asc
        });

    }

    return {

        //main function to initiate the module
        init: function () {
            if (!jQuery().dataTable) {
                return;
            }

            initTable1();
        }

    };

}();

if (App.isAngularJsApp() === false) { 
    jQuery(document).ready(function() {
        TableDatatablesManaged.init();
    });
}